element.calculation:
(function calculatedFieldValue(current) {
    var nota1 = parseFloat(current.nota_01);
    var nota2 = parseFloat(current.nota_02);
 
    var hasNota1 = !isNaN(nota1);
    var hasNota2 = !isNaN(nota2);
 
    if (hasNota1 && hasNota2) {
        return (nota1 + nota2) / 2;
    } else if (hasNota1) {
        return nota1;
    } else if (hasNota2) {
        return nota2;
    } else {
        return null;
    }
})(current);
